import React from 'react';
import axios from 'axios';

class Edit extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      Title: '',
      Year: '',
      Poster: '',
      _id: ''
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleTitleChange = this.handleTitleChange.bind(this);
    this.handleYearChange = this.handleYearChange.bind(this);
    this.handlePoster = this.handlePoster.bind(this);
  }
  componentDidMount() {
    alert(this.props.match.params.id);

    axios.get('http://localhost:4000/api/shows/' + this.props.match.params.id)
      .then((response) => {
        this.setState({
          _id: response.data._id,
          Title: response.data.title,
          Year: response.data.year,
          Poster: response.data.poster
        })
      })
      .catch();


  }
  //edit functions for title , year and poster
  handleTitleChange(e) {
    this.setState({ Title: e.target.value });
  }

  handleYearChange(e) {
    this.setState({ Year: e.target.value });
  }

  handlePoster(e) {
    this.setState({ Poster: e.target.value });
  }

  handleSubmit(e) {
    alert(this.state.Title + "      " + this.state.Year
      + "       " + this.state.Poster);
    e.preventDefault();

    const newShow = {
      title: this.state.Title,
      year: this.state.Year,
      poster: this.state.Poster
    };

    axios.put('http://localhost:4000/api/shows/' + this.state._id,
      newShow)
      .then()
      .catch();


    this.setState({
      Title: '',
      Year: '',
      Poster: ''
    });
  }


  render() {
    return (
      <div>
        <h1>Edit component</h1>
        <form onSubmit={this.handleSubmit}>
          <div className='form-group'>
            <label>Show Title</label>
            <input
              type='text'
              className='form-control'
              value={this.state.Title}
              onChange={this.handleTitleChange}
            ></input>
          </div>
          <div className='form-group'>
            <label>Show Year</label>
            <input
              type='text'
              className='form-control'
              value={this.state.Year}
              onChange={this.handleYearChange}
            ></input>
          </div>
          <div className='form-group'>
            <label>Show Poster Url</label>
            <textarea
              row='3'
              className='form-control'
              value={this.state.Poster}
              onChange={this.handlePoster}
            ></textarea>
          </div>
          <div>
            <input
              type="submit"
              value="Edit Show">
            </input>
          </div>
        </form>
      </div>
    )
  }
}

export default Edit;
