import React from 'react';
import axios from 'axios';
import { newExpression } from '@babel/types';

class addSportScandal extends React.Component {
  constructor(props){
    super(props);

    this.state = {scandalName:'',
                  Date:'',
                Poster:'',
              Base64Image:''};

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleScandalNameChange = this.handleScandalNameChange.bind(this);
    this.handleDateChange = this.handleDateChange.bind(this);
    this.handlePoster = this.handlePoster.bind(this);
    this.handleImageChange = this.handleImageChange.bind(this);
  }
  //functions for adding to database
  handleScandalNameChange(e){
    this.setState({scandalName: e.target.value});
  }

  handleDateChange(e){
    this.setState({Date: e.target.value});
  }

  handlePoster(e){
    this.setState({Poster: e.target.value});
  }

  getBase64(file, cb) {
    let reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = function () {
        cb(reader.result)
    };
    reader.onerror = function (error) {
        console.log('Error: ', error);
    };
}

  handleImageChange(e){
    alert(e.target.files[0]);
    this.getBase64(e.target.files[0], (base64) =>{
        this.setState({Base64Image:base64});
    })
  }

  handleSubmit(e){
    alert(this.state.scandalName+ "      " + this.state.Date 
    +"       "+ this.state.Poster);
    e.preventDefault();
    
                const newShow = {
                  title: this.state.scandalName,
                  year: this.state.Date,
                  poster: this.state.Poster
                };
          axios.post('http://localhost:4000/api/shows',newShow) 
          .then()
          .catch();
          

          this.setState({scandalName:'',
                Date:'',
                Poster:''});    
  }

  render() {
    return (
      <div>
        <font color="gold"><label><h1>Know any interesting Sport Scandals ?? Go ahead and add them to the page </h1></label></font> 
        <form onSubmit={this.handleSubmit}>
        <div className='form-group'>
        <font color="black"><label><h4>Scandal Name :</h4></label></font>
          <input
          type='text'
          className='form-control'
          value={this.state.scandalName}
          onChange={this.handleScandalNameChange}
          ></input>
        </div>
        <div className='form-group'>
        <font color="black"><label><h4>Date of Scandal :</h4></label></font>
          <input
          type='text'
          className='form-control'
          value={this.state.Date}
          onChange={this.handleDateChange}
          ></input>
        </div>
        <div className='form-group'>
        <font color="black"><label><h4>Show Poster Url</h4></label></font>
          <textarea
          row='3'
          className='form-control'
          value={this.state.Poster}
          onChange={this.handlePoster}
          ></textarea>
        </div>
        <div>
          <font color="black"><label><h4>Upload Poster From Your PC</h4></label></font>
          <input
          type='file'
          className='form-control'
          onChange={this.handleImageChange}
          ></input>

        </div>
        <div>
          <input
          type="submit"
          value="Add Scandal">
          </input>
        </div>
        </form>
        <img src={this.state.Base64Image}></img>
        <br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br>
      </div>
      
    );
  }
}

export default addSportScandal;
